public boolean validate()
{
    boolean ret = true;

    if (!purchTable.checkAccountingDateFiscalYear())
    {
        purchTable.AccountingDate = purchTable.orig().AccountingDate;
        ret = false;
        Box::warning("@SYS335906");
    }
    else
    {
        ret = super();
    }

    return ret;
}

