        purchTableForm.setAllowVisibleReductionEntryFields_JP(purchTable, purchLine, reductionEntry_JP);
        PurchLine_ds.research();
    }

    purchline_ds.enableDistributionTemplate();

    element.enableLedgerDimensionDefaultAccount();
}

